import "./App.css";
import Table from "./components/Table";
import SearchBar from "./components/SearchBar";
import { React, useEffect ,useState} from "react";

function App() {
  const [transactions, setTransactions] = useState([])

  function filterTransaction(query){
    // fetch("http://localhost:3004/transactions")
    //   .then((req) => req.json())
    //   .then((data) => {
    //     const newList = data.filter(transaction => transaction.category.toLowerCase().includes(query.toLowerCase()));
    //     setTransactions(newList)
    //   });
    const newList = transactions.filter(transaction => transaction.category.toLowerCase().includes(query.toLowerCase()))
    setTransactions()
  }

  useEffect(() => {
    fetch("http://localhost:3004/transactions")
      .then((req) => req.json())
      .then((data) => {
        setTransactions(data)
        // console.log(data);
      });
  }, []);

  return (
    <div className="container p-5">
      <header>
        <h1>FlatironBankApp</h1>
      </header>
      <SearchBar onSearchQuery={query => filterTransaction(query)}/>
      <Table transactions={transactions}/>
    </div>
  );
}

export default App;
